<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Gothic Vampire UI</title>
    <link href="https://fonts.googleapis.com/css2?family=Cinzel+Decorative:wght@700&family=Pirata+One&display=swap" rel="stylesheet">
    <style>
        body {
            margin: 0;
            padding: 0;
            height: 100vh;
            display: flex;
            justify-content: center;
            align-items: center;
            background-color: #0a0000;
            background-image:
                radial-gradient(circle at 10% 20%, rgba(100, 0, 0, 0.1) 0%, transparent 40%),
                radial-gradient(circle at 90% 80%, rgba(100, 0, 0, 0.1) 0%, transparent 40%);
            overflow: hidden;
        }

        .container {
            position: relative;
            width: 80vw;
            max-width: 600px;
            aspect-ratio: 2 / 1;
        }

        .gothic-frame {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            border: 2px solid #8b0000;
            box-shadow:
                0 0 20px rgba(139, 0, 0, 0.4),
                0 0 40px rgba(0, 0, 0, 0.5) inset;
            border-radius: 5px;
            overflow: hidden;
            z-index: 1;
        }

        .gothic-frame::before {
            content: "";
            position: absolute;
            top: 10px;
            left: 10px;
            right: 10px;
            bottom: 10px;
            border: 1px solid rgba(139, 0, 0, 0.6);
            box-shadow: 0 0 10px rgba(139, 0, 0, 0.3);
            z-index: -1;
        }

        .corner {
            position: absolute;
            width: 30px;
            height: 30px;
            z-index: 2;
        }

        .top-left {
            top: -5px;
            left: -5px;
            border-top: 3px solid #8b0000;
            border-left: 3px solid #8b0000;
            box-shadow: -5px -5px 10px rgba(0, 0, 0, 0.5);
        }

        .top-right {
            top: -5px;
            right: -5px;
            border-top: 3px solid #8b0000;
            border-right: 3px solid #8b0000;
            box-shadow: 5px -5px 10px rgba(0, 0, 0, 0.5);
        }

        .bottom-left {
            bottom: -5px;
            left: -5px;
            border-bottom: 3px solid #8b0000;
            border-left: 3px solid #8b0000;
            box-shadow: -5px 5px 10px rgba(0, 0, 0, 0.5);
        }

        .bottom-right {
            bottom: -5px;
            right: -5px;
            border-bottom: 3px solid #8b0000;
            border-right: 3px solid #8b0000;
            box-shadow: 5px 5px 10px rgba(0, 0, 0, 0.5);
        }

        .ornament {
            position: absolute;
            color: #8b0000;
            font-size: 24px;
            opacity: 0.8;
            text-shadow: 0 0 5px rgba(139, 0, 0, 0.5);
        }

        .o1 { top: 5px; left: 50%; transform: translateX(-50%); }
        .o2 { bottom: 5px; left: 50%; transform: translateX(-50%); }
        .o3 { left: 5px; top: 50%; transform: translateY(-50%); }
        .o4 { right: 5px; top: 50%; transform: translateY(-50%); }

        .background-pattern {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background-image:
                repeating-linear-gradient(45deg, transparent, transparent 10px, rgba(139, 0, 0, 0.03) 10px, rgba(139, 0, 0, 0.03) 20px),
                repeating-linear-gradient(-45deg, transparent, transparent 10px, rgba(139, 0, 0, 0.03) 10px, rgba(139, 0, 0, 0.03) 20px);
            z-index: -1;
        }

        .blood-drop {
            position: absolute;
            width: 6px;
            height: 15px;
            background: linear-gradient(to bottom, rgba(139, 0, 0, 0.7), rgba(139, 0, 0, 0.9));
            border-radius: 50% 50% 50% 50% / 60% 60% 40% 40%;
            animation: drip 8s infinite ease-in-out;
            opacity: 0;
            box-shadow: 0 0 5px rgba(139, 0, 0, 0.5);
        }

        @keyframes drip {
            0%, 100% { opacity: 0; transform: translateY(0); }
            10% { opacity: 0.9; }
            80% { opacity: 0.9; transform: translateY(100px); }
            90% { opacity: 0; }
        }

        .text-container {
            position: relative;
            z-index: 10;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100%;
            padding: 20px;
            box-sizing: border-box;
        }

        .main-text {
            font-family: 'Cinzel Decorative', cursive;
            font-size: clamp(24px, 8vw, 64px);
            color: #d10000;
            text-shadow:
                0 0 5px rgba(139, 0, 0, 0.8),
                0 0 10px rgba(0, 0, 0, 0.8);
            text-align: center;
            letter-spacing: 2px;
            transform: translateY(-5px);
            background: linear-gradient(to bottom, #ff0000, #800000);
            -webkit-background-clip: text;
            background-clip: text;
            color: transparent;
            position: relative;
            display: inline-block;
        }

        .main-text::before {
            content: "Love&Power";
            position: absolute;
            left: 0;
            top: 0;
            z-index: -1;
            color: rgba(0, 0, 0, 0.5);
            filter: blur(4px);
            transform: translateY(2px);
        }

        .symbol {
            position: absolute;
            width: 100%;
            height: 100%;
            overflow: hidden;
            z-index: 0;
        }

        .symbol::before, .symbol::after {
            content: "";
            position: absolute;
            width: 200px;
            height: 200px;
            border: 1px solid rgba(139, 0, 0, 0.2);
            border-radius: 50%;
        }

        .symbol::before {
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
        }

        .symbol::after {
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%) rotate(45deg);
        }

        .pentagram {
            position: absolute;
            width: 100px;
            height: 100px;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            opacity: 0.1;
        }

        .separator {
            display: inline-block;
            color: #8b0000;
            font-size: 0.8em;
            vertical-align: middle;
            margin: 0 5px;
            animation: pulse 2s infinite ease-in-out;
        }

        @keyframes pulse {
            0%, 100% { opacity: 0.7; }
            50% { opacity: 1; }
        }
    </style>
</head>
<body>
    <div class="container">
        <!-- Gothic Frame -->
        <div class="gothic-frame">
            <div class="corner top-left"></div>
            <div class="corner top-right"></div>
            <div class="corner bottom-left"></div>
            <div class="corner bottom-right"></div>

            <div class="ornament o1">❧</div>
            <div class="ornament o2">☙</div>
            <div class="ornament o3">❦</div>
            <div class="ornament o4">❧</div>
        </div>

        <!-- Background Pattern -->
        <div class="background-pattern"></div>

        <!-- Blood Drops -->
        <div class="blood-drop" style="left: 10%; animation-delay: 0s;"></div>
        <div class="blood-drop" style="left: 30%; animation-delay: 3s;"></div>
        <div class="blood-drop" style="left: 50%; animation-delay: 1s;"></div>
        <div class="blood-drop" style="left: 70%; animation-delay: 5s;"></div>
        <div class="blood-drop" style="left: 90%; animation-delay: 2s;"></div>

        <!-- Mysterious Symbols -->
        <div class="symbol"></div>

        <!-- Text Content -->
        <div class="text-container">
            <div class="main-text">Love<span class="separator">&</span>Power</div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // Add subtle animation for gothic atmosphere
            document.querySelectorAll('.corner').forEach(corner => {
                corner.style.transition = 'all 3s ease-in-out';
                setInterval(() => {
                    corner.style.boxShadow = `${Math.random() * 2 - 1}px ${Math.random() * 2 - 1}px 15px rgba(139, 0, 0, ${Math.random() * 0.3 + 0.3})`;
                }, 3000);
            });

            // Make ornaments glow periodically
            document.querySelectorAll('.ornament').forEach(ornament => {
                setInterval(() => {
                    ornament.style.textShadow = `0 0 ${Math.random() * 5 + 5}px rgba(139, 0, 0, ${Math.random() * 0.5 + 0.5})`;
                    setTimeout(() => {
                        ornament.style.textShadow = '0 0 5px rgba(139, 0, 0, 0.5)';
                    }, 500);
                }, Math.random() * 5000 + 3000);
            });
        });
    </script>
</body>
</html>
