/* 果冻风格下拉框 */
select {
    --jelly-color: #EFCCDA;   /* 主色 */
    --shadow-color: #454545; /* 投射色 */
    background: 
        linear-gradient(145deg, 
            rgba(255,255,255,0.4) 10%, 
            rgba(239,204,218,0.6) 90%) !important;
    border: px solid rgba(45,45,45,0.15) !important;
    border-radius: 15px !important;
    box-shadow:
        0 2px 20px -4px rgba(69,69,69,0.2),
        inset 0 4px 4px rgba(255,255,255,0.4),
        inset 0 -2px 4px rgba(69,69,69,0.1) !important;
    transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94) !important;
    padding: px 36px 12px 16px !important;
    color: #454545 !important;
    backdrop-filter: blur(8px);
    position: relative;
}

/* 果冻材质层 */
select::after {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    border-radius: 10px;
    background: 
        linear-gradient(135deg, 
            rgba(255,255,255,0.6) 0%, 
            rgba(239,204,218,0.3) 100%);
    mix-blend-mode: overlay;
    pointer-events: none;
}




/* 聚焦状态微调 */
input[type="text"]:focus,
input[type="number"]:focus,
input[type="switch"]:focus,
input:not([type]):focus,
textarea:not([type="search"]):focus {
    border-color: #EFCCDA !important; /* 加深边框色 */
    box-shadow: 0 0 0 2px rgba(141, 222, 225, 0.3) !important;
}






/* 自定义勾选框配色 */
input[type="checkbox"] {
    --checkbox-bg: #454545;  /* 基础背景色 */
    --checkbox-accent: #EFCCDA; /* 强调色 */

    background-color: var(--checkbox-bg) !important;
    box-shadow: 0 0 0 px var(--checkbox-accent) !important;
    transition: all 0.2s ease;
}

/* 选中状态 */
input[type="checkbox"]:checked {
    background-color: var(--checkbox-accent) !important;
    box-shadow: 0 0 0 1px var(--checkbox-bg) !important;
}

/* 对勾符号 */
input[type="checkbox"]::before {
    box-shadow: inset 1em 1em var(--checkbox-bg) !important;
    filter: contrast(0.1);
}

/* 爱心图标 */
input[type="checkbox"]:checked::after {
    content: "❤︎";
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    font-size: 12px;
    line-height: 1;
    color: #454545;
    text-shadow: 0 2px 4px rgba(255,107,107,0.2);
}


/* 果冻立体风格 EFCCDA+454545 配色方案 */
:root {
  --jelly-bg: #FFEDF5;         /* 果冻基底色 */
  --jelly-accent: #454545;     /* 立体投射色 */
  --jelly-shadow: rgba(69, 69, 69, 0.15);
  --jelly-highlight: rgba(255, 255, 255, 0.9);
}

/* 果冻立体核心样式 */
.inline-drawer-header,
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4,
.standoutHeader {
  background: linear-gradient(
    145deg,
    var(--jelly-bg) 30%,
    color-mix(in srgb, var(--jelly-bg) 90%, white 10%)
  ) !important;
  border: px solid var(--jelly-accent) !important;
  box-shadow: 
    0 4px 18px var(--jelly-shadow),
    inset 2px 2px 4px var(--jelly-highlight),
    inset -2px -2px 4px color-mix(in srgb, var(--jelly-accent) 20%, transparent) !important;
  border-radius: 12px;
  backdrop-filter: blur(4px);
  transition: all 0.2s cubic-bezier(0.34, 1.56, 0.64, 1);
  position: relative;
  overflow: hidden;
}

/* 果冻材质伪元素 */
.standoutHeader::after,
.inline-drawer-header::after {
  content: "";
  position: absolute;
  top: -50%;
  left: -50%;
  width: 200%;
  height: 200%;
  background: radial-gradient(
    circle at 50% 50%,
    rgba(255, 255, 255, 0.4) 10%,
    transparent 60%
  );
  transform: rotate(15deg);
  pointer-events: none;
}

/* 立体文字样式 */
.standoutHeader strong,
.inline-drawer-header h4 {
  color: var(--jelly-accent) !important;
  text-shadow: 
    1px 1px 0 color-mix(in srgb, white 60%, var(--jelly-bg)),
    -1px -1px 1px var(--jelly-shadow);
  font-weight: 700;
  letter-spacing: 0.8px;
  position: relative;
  z-index: 2;
}




