custom_css:
/*
@DISCORD & XIAOHONGSHU: 搞砸了小羊
字体和配色
*/
@import url("https://fontsapi.zeoseven.com/281/main/result.css");

body {
    font-family: "Xiaolai SC";
    font-weight: normal;
}

:root {/* yang-c711 */
  --white: #ffffff;
  --lighter: #f0faff;
  --light: #d8ebf9;
  --mid: #c2e2f6;
  --dark: #7DA5CA;
  --lightgrey: #acaaab;
  --black:#272920;
}

body code {
  border-left: 3px solid var(--mid) !important;
  display: inline-block !important;
  vertical-align: middle !important;
  font-family: 'Xiaolai SC', sans-serif !important; 
  color: var(--dark) !important; 
  font-size: 13px !important;
  font-weight: normal !important;
  line-height: 1.7 !important;
  background-color: transparent !important; 
  border-top: none !important;       
  border-right: none !important;      
  border-bottom: none !important;     
  border-radius: 0 !important;        
  padding: 0 0 0 0.6em !important;    
  white-space: pre-wrap !important;   
}

/*下拉框样式*/
.range-block-counter input,
.neo-range-input,
.text_pole,
.widthNatural,
.drawer-content select,
.select2-container .select2-selection--multiple, .select2-container .select2-selection--single {
  background-color: var(--light);
  border: 1px solid var(--white);
  box-shadow: 2px 2px 1px var(--light);
}

/*下拉框选定*/
select:focus {
  box-shadow: 2px 2px 2px var(--light);
}

/*搜索框 角色定义框*/
textarea {
  background-color: var(--light);
  border: 1px solid var(--white);
  box-shadow: 2px 2px 1px var(--light);
}

/*复选框样式*/
input[type='checkbox'] {
  background-color: var(--white);
  border: 1px solid var(--mid);
}

input[type='checkbox']:checked {
  background-color: var(--dark);
}

input[type='checkbox']:checked::after {
  background-color: var(--white);
}

/*滑块样式*/
input[type="range"]::-webkit-slider-thumb {
  position: relative;
  height: 30px; 
  width: 30px; 
  box-shadow: none;
  background: transparent url(https://files.catbox.moe/x278ys.png) no-repeat center/25px 25px;
}

/*菜单标题*/
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4,
.standoutHeader {
  position: relative;
  background-color: var(--light);
  border: 1px solid var(--white);
  box-shadow: 2px 2px 1px var(--light);
}

/*下拉框标题样式*/
.inline-drawer-header,
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4,
.standoutHeader {
  border: 1px solid var(--white);
  background-image: var(--light); 
  filter: none !important; 
  box-shadow: 2px 2px 1px var(--light);
}

.fillLeft .scrollableInner {
  padding-right: 30px !important;
}

.range-block {
  border: 1px solid var(--white);
  color: var(--grey) !important;
  -webkit-backdrop-filter: blur(10px); 
  backdrop-filter: blur(10px);
  box-shadow: 2px 2px 1px var(--light);
}

/* 预设页 禁用选项 */
input:disabled, 
textarea:disabled {
  filter: none !important;
  background-color: var(--light);
  border: 1px solid var(--white);
  box-shadow: 2px 2px 1px var(--light);
}

/* 预设页 禁用选项 文字颜色 */
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name, #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
  color: var(--dark) !important;
}

/*字体页 输入+菜单 样式*/
#AdvancedFormatting .disabled {
  filter: none !important;
}

/* 背景图片页固定大小 */
#bg_menu_content {
  flex-grow: 1;      
  flex-shrink: 0;    
  flex-basis: 250px; 
  min-height: 150px; 
  display: flex;
  flex-wrap: wrap; 
  justify-content: space-around; 
  align-items: center; 
  padding: 10px; 
  box-sizing: border-box; 
}

.drawer-content {
  border-top-right-radius: 0px;
  border-top-left-radius: 0px;
  box-shadow: 0 1px 0 0 var(--lighter);
  padding-top: 8px;
  background-image: url("https://files.catbox.moe/n6q1m2.jpg");
  background-size: 100% auto;
  background-repeat: repeat-y;
  background-position: center center;
}

/* icon bar */
body.no-blur #top-bar {
  box-shadow: none;
  background-color: transparent; 
 }

 #top-settings-holder {
  background-image: url('https://files.catbox.moe/lwppk5.jpg');
  background-color: transparent; 
  height: 50px;
  background-size: auto 45px; 
  background-repeat: repeat-x;
  background-position: top left;
  box-shadow: none;
 }

 #leftNavDrawerIcon::before,
 #API-status-top::before,
 .drawer-icon.fa-font::before,
 #WIDrawerIcon::before,
 .drawer-icon.fa-user::before,
 .fa-user-cog::before,
 #site_logo .drawer-icon::before,
 #table_database_settings_drawer .fa-bars-progress::before,
 .drawer-icon.fa-cubes::before,
 #persona-management-button .drawer-icon.fa-face-smile::before,
 #rightNavHolder #rightNavDrawerIcon::before {
     content: '' !important;
     display: block !important;
     background-size: contain !important;
     background-repeat: no-repeat !important;
     background-position: center center !important;
     border: none !important;
     visibility: visible !important;
 }


 /*无记忆表格，9个图标*/

 /* A组 - 大图标 (图案A, 25px) */
 #leftNavDrawerIcon::before,
 .drawer-icon.fa-font::before,
 .drawer-icon.fa-user::before,
 .drawer-icon.fa-cubes::before {
     background-image: url('https://files.catbox.moe/7e39mj.png') !important;
     width: 25px !important;
     height: 25px !important;
     margin-top: -20px !important;
 }

 /* A组 - 大图标 (图案A, 25px) */
 .fa-user-cog::before,
 #rightNavHolder #rightNavDrawerIcon::before { /* 在9图标状态下，它是奇数位(A)，小尺寸 */
     background-image: url('https://files.catbox.moe/7e39mj.png') !important;
     width: 25px !important;
     height: 25px !important;
     margin-top: -12px !important;
 }

 /* B组 - 小图标 (图案B, 18px) */
 #persona-management-button .drawer-icon.fa-face-smile::before { /* 在9图标状态下，它是偶数位(B)，大尺寸 */
     background-image: url('https://files.catbox.moe/386i40.png') !important;
     width: 18px !important;
     height: 18px !important;
     margin-top: -12px !important;
 }

 /* B组 - 小图标 (图案B, 18px) */
 #API-status-top::before,
 #WIDrawerIcon::before,
 #site_logo .drawer-icon::before {
     background-image: url('https://files.catbox.moe/386i40.png') !important;
     width: 18px !important;
     height: 18px !important;
     margin-top: -12px !important;
 }


 /*有记忆表格，10个图标*/

 /* 记忆表格图标本身: 第8位(B)，小尺寸 */
 #table_database_settings_drawer .fa-bars-progress::before {
     background-image: url('https://files.catbox.moe/386i40.png') !important;
     width: 18px !important;
     height: 18px !important;
     margin-top: -12px !important;
 }

 /* 笑脸图标被推到第9位(A)，大尺寸 */
 #table_database_settings_drawer ~ #persona-management-button .drawer-icon.fa-face-smile::before {
     background-image: url('https://files.catbox.moe/7e39mj.png') !important;
     width: 25px !important;
     height: 25px !important;
     margin-top: -20px !important;
 }

 /* 右侧导航图标被推到第10位(B)，小尺寸 */
 #table_database_settings_drawer ~ #rightNavHolder #rightNavDrawerIcon::before {
     background-image: url('https://files.catbox.moe/386i40.png') !important;
     width: 18px !important;
     height: 18px !important;
     margin-top: -12px !important;
 }

.drawer-icon {
    opacity: 1 !important;
    transition: opacity 0.2s ease !important;
}

.fa-ellipsis-v::before {
    content: '' !important;
    display: none !important;
}

body.no-blur #send_form {
  background-color: transparent !important;
  border: none !important;
  box-shadow: none;
}

.fa-bars {
    background: url('https://files.catbox.moe/jbynml.png') center/contain no-repeat !important;
    width: 25px !important;
    height: 25px !important;
    margin-left: 7px !important;
    margin-top: 3px !important;
}

#extensionsMenuButton {
    background: url('https://files.catbox.moe/eoz3ki.png') center/contain no-repeat !important;
    width: 25px !important;
    height: 25px !important;
    margin-left: 3px !important;
    margin-top: 3px !important;
}

.fa-solid.fa-rocket,
.fa-solid.fa-platte,
.fa-solid.fa-star-and-crescent,
.fa-solid.fa-star-of-david {
filter: none !important;
opacity: 1 !important;
background: url('https://files.catbox.moe/tkdijy.png') center/contain no-repeat ;
width: 25px ;
height: 25px ;
margin-top: 2px;
}

.fa-paper-plane {
    background: url('https://files.catbox.moe/6zssod.png') center/contain no-repeat !important;
    width: 25px !important;
    height: 25px !important;
    margin-right: 6px !important;
    margin-top: 5px !important;
}

.fa-solid.fa-user-secret {
  background: url('wu') center/contain no-repeat !important;
  width: 25px !important;
  height: 25px !important;
  margin-top: 3px !important;
}

.fa-solid.fa-circle-stop {
  filter: none !important;
  background: url('https://files.catbox.moe/e5qwvk.gif') center/contain no-repeat !important;
  width: 30px !important;
  height: 30px !important;
  margin-right: 6px !important;
  margin-top: -1px !important;
  transition-duration: 0s;
}

.fa-solid.fa-circle-stop::before,
.fa-bars::before,
.fa-ellipsis-v::before,
.fa-paper-plane::before,
.fa-solid.fa-user-secret::before,
#extensionsMenuButton::before,
.fa-solid.fa-rocket::before,
.fa-solid.fa-platte::before,
.fa-solid.fa-star-and-crescent::before,
.fa-solid.fa-star-of-david::before {
  filter: none !important;
  content: '' !important;
  display: none !important;
}

.drawer-icon,
.fa-bars,
#extensionsMenuButton,
.fa-paper-plane,
.fa-solid.fa-user-secret,
.fa-solid.fa-circle-stop,
#quick-reply-rocket-button,
#quick-reply-platte-button,
#quick-reply-star-and-crescent-button,
#quick-reply-star-of-david-button {
    filter: none !important;
    opacity: 1 !important;
    transition: opacity 0.2s ease !important;
}

.mes_button.extraMesButtonsHint,
.mes_button.mes_edit {
    width: 20px;
    height: 20px;
    background: no-repeat center / contain;
    opacity: 1;
    filter: none;
    font-size: 0;
    filter: drop-shadow(1px 1px 1px var(--mid));
}

.mes_button.extraMesButtonsHint {
    background-image: url(https://files.catbox.moe/9ar04u.jpg);
}

.mes_button.mes_edit {
    background-image: url(https://files.catbox.moe/qa7qzg.jpg);
}
/* message box */
::-webkit-scrollbar {
  width: 0px;
  height: 0px;
  background: transparent;
 }

body #chat .mes .mesAvatarWrapper,
body #chat .last_mes .mesAvatarWrapper {
  display: flex !important;
  align-items: center !important;
  position: static !important;
  padding: 0 !important;
  width: 0px !important;
  height: 0px !important;
  pointer-events: auto !important;
}

.mes:not([is_user="true"]) .mes_timer,
.mes:not([is_user="true"]) .mesIDDisplay,
.mes:not([is_user="true"]) .tokenCounterDisplay {
  position: absolute !important;
  top: 20px !important; 
  display: inline-block !important;
  pointer-events: none;
}

.mes:not([is_user="true"]) .mes_timer {
  left: calc(50% - 87px) !important;
  min-width: 50px !important;
  margin-left: 0 !important; 
}

.mes:not([is_user="true"]) .mesIDDisplay {
  left: calc(50% - 25px) !important;
  min-width: 50px !important;
}

.mes:not([is_user="true"]) .tokenCounterDisplay {
  left: calc(50% + 37px) !important;
  min-width: 50px !important;
  margin-right: 0 !important; 
}

.mes[is_user="true"] .mes_timer,
.mes[is_user="true"] .mesIDDisplay,
.mes[is_user="true"] .tokenCounterDisplay {
  position: absolute !important;
  top: 10px !important; 
  display: inline-block !important;
  pointer-events: none;
}

.mes[is_user="true"] .mes_timer {
  left: calc(50% - 87px) !important;
  min-width: 50px !important;
  margin-left: 0 !important; 
}

.mes[is_user="true"] .mesIDDisplay {
  left: calc(50% - 25px) !important;
  min-width: 50px !important;
}

.mes[is_user="true"] .tokenCounterDisplay {
  left: calc(50% + 37px) !important;
  min-width: 50px !important;
}

.mes .mes_timer::before,
.mes .mesIDDisplay::before,
.mes .tokenCounterDisplay::before {
  content: '';
  display: inline-block;
  position: absolute;
  left: -14px;
  top: 50%;
  transform: translateY(-50%);
  width: 14px;
  height: 14px;
  background-image: url('https://files.catbox.moe/u6fj4c.png');
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center;
  filter: drop-shadow(1px 1px 1px var(--mid));
}

.mes[is_user="true"] .mes_timer::before {
  display: none;
}

 .mes .avatar,
 .last_mes .avatar {
   position: absolute;
   bottom: 20px;
   left: 37%; 
   width: 50px; 
   height: 50px; 
   border-radius: 50%; 
   object-fit: cover; 
   z-index: 1; 
 }

.mes[is_user="false"] .avatar::before,
.last_mes[is_user="false"] .avatar::before {
    background-image: url('https://files.catbox.moe/u2bdbs.gif'); 
}

.mes .avatar::before,
.last_mes .avatar::before {
   content: ""; 
   position: absolute; 
   top: -13px;    
   left: -10px;   
   width: 70px; 
   height: 70px; 
   background-image: url('https://files.catbox.moe/wwbycp.gif'); 
   background-size: cover; 
   background-position: center; 
   background-repeat: no-repeat; 
   border-radius: 50%; 
   z-index: 2; 
   transform: translateZ(-1px); 
}

.name_text {
  position: absolute;
  bottom: 35px;
  left: 54%; 
  padding-left: 10px;
  box-sizing: border-box;
  color:#9fb4c7;
}

.name_text::before {
  content: ""; 
  position: absolute;
  left: 0; 
  top: 50%; 
  transform: translateY(-50%); 
  width: 2px; 
  height: 80%; 
  background-color:#9fb4c7; 
}

.mes,
.last_mes { 
  position: relative !important;
  margin-top: 30px;
  border: 6px solid var(--white); 
  box-shadow:
    5px 5px 7px var(--lightgrey), 
    -5px -5px 10px var(--lighter), 
    inset 0 0 15px 10px var(--light); 
  border-radius: 30px;    
  background-image: url('https://files.catbox.moe/n6q1m2.jpg'); 
  background-repeat: repeat-y; 
  background-size: 100% auto;
  background-position: center center; 
  z-index: 1;
}

.mes_text {
  margin-top: 10px;
  margin-bottom: 80px;
  padding:0px;
 }

 #chat {
  top: 0 !important;
  bottom: 0 !important;
  border:none !important;
  padding: 0px 10px 15px 10px !important;
  background-color: transparent !important;
 }

 #bg_custom {
  background-image: none !important;
  z-index: -2 !important;
 }

 #bg1 {
  background-image: url('https://files.catbox.moe/dqnkn8.jpeg') !important; /* 将 '您的图片URL' 替换为实际的图片链接 */
  background-size: cover !important; 
  background-position: center !important;
  background-repeat: no-repeat !important;
  background-attachment: fixed !important;
  z-index: -3 !important;
}

.mes_block {
  position: relative !important;
  overflow: visible !important;
  z-index: 2 !important;
  padding: 7px;
}

#send_textarea {
  background-color: var(--light) !important;
  border-left: 1px solid var(--mid) !important;
  border-right: 1px solid var(--mid) !important;
}

.mes_reasoning_header {
  padding-left: 30px; 
  background-image: url('https://files.catbox.moe/x21ymg.gif');
  background-color: var(--light); 
  background-size: 20px 20px;
  background-position: left 5px center;
  background-repeat: no-repeat;
  -webkit-backdrop-filter: blur(8px); 
  backdrop-filter: blur(8px);         
}

.reasoning_edit_textarea, .edit_textarea {
  background-color: #d8ebf950;
}
